from django.urls import pathfrom app import viewsfrom django.contrib import adminfrom django.conf import settingsfrom django.conf.urls.static import staticfrom askme_karganov.settings import DEBUGurlpatterns = [    path('', views.index, name='index'),    path('hot/', views.hot, name='hot'),    path('ask/', views.ask, name='ask'),    path('settings/', views.settings, name='settings'),    path('login/', views.log_in, name='log_in'),    path('logout/', views.logout, name='logout'),    path('register/', views.register, name='register'),    path('tag/<int:tag_id>', views.tag_page, name='tag-page'),    path('question/<int:question_id>', views.question, name= 'question'),    path('<int:question_id>/like_async', views.like_async, name='like_async'),    path('hot/<int:question_id>/like_async', views.like_async_hot, name='like_async'),    path('questions/<int:answer_id>/like_async_answer', views.like_async_answer, name='like_async_answer'),    path('questions/<int:answer_id>/checkbox_async_answer', views.checkbox_async_answer, name='check_async_answer'),]if DEBUG:    urlpatterns += static(settings.MEDIA_URL, document_root=settings.MEDIA_ROOT)