from django.urls import pathfrom app import viewsfrom django.contrib import adminfrom django.conf import settingsfrom django.conf.urls.static import staticfrom askme_karganov.settings import DEBUGurlpatterns = [    path('', views.index, name='index'),    path('hot/', views.hot, name='hot'),    path('ask/', views.ask, name='ask'),    path('settings/', views.settings, name='settings'),    path('login/', views.log_in, name='log_in'),    path('logout/', views.logout, name='logout'),    path('register/', views.register, name='register'),    path('tag/<int:tag_id>', views.tag_page, name='tag-page'),    path('question/<int:question_id>', views.question, name= 'question')]if DEBUG:    urlpatterns += static(settings.MEDIA_URL, document_root=settings.MEDIA_ROOT)